test_data = ""

def communication_decoder(index_input):

   starterpack = str(test_data[0:index_input])
   data = str(test_data[index_input::])
   index = index_input

   for letter in data:
      
      starterpack += letter
      starterpack = starterpack[1::]
      isunique = len(set(starterpack)) != len(starterpack)
         
      if isunique == True:
         index += 1
            
      else:
         index += 1
         return index


print(f"Part 1: {communication_decoder(4)}")
print(f"Part 2: {communication_decoder(14)}")

